import '/home/dinashboul/React_contextApi/myapp/src/componenets/Home/homestyle.css'
import axios from 'axios'
import { Link } from 'react-router-dom/cjs/react-router-dom.min'
import Fetching from '../Fetching'
import { useAuth } from '../../contexts/AuthContext'
function Home() {

const {user}=useAuth()
const {data}=Fetching('https://store-wbly.onrender.com/items');
console.log("the data is",data,data.user_id)

const handleAddToCart = async (e) => {
  e.preventDefault()

  const configuration = {
    method: "Put",
    url: "https://store-wbly.onrender.com/user/" + data.user_id,
    data: {
      image_url
    }

  }
  await axios(configuration)
    .then((result) => {
      console.log("the Update of image success", result.data)
      setIsUpdate(true)
    })
    .catch((err) => {
      err = new Error()
      console.log("no data")
    })
}



  return (
    <section className="articles">
{data && data.map(item => (
  <article key={item.item_id}>
    <div className="article-wrapper">
      <figure>
        <img src={item.image_url} alt="" />
      </figure>
      <div className="article-body">
        {user ?(
      <button
      onClick={handleAddToCart}
      >Add To Cart</button>
      ):(<></>)}
        <h2>{item.name}</h2>
        <h2>
        {item.categories}
        </h2>
        <h2>
        {item.description}
        </h2>
        <Link to="/login" className="read-more" >
          Price is {item.price}
        </Link>
      </div>
    </div>
  </article>
))}
  </section>




  )

}



export default Home;
