import React, { useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min'
import "/home/dinashboul/React_contextApi/myapp/src/componenets/Users/usr.css"
import axios from 'axios';
import Model from './Model';

const UserCart = ({ email }) => {
  console.log("the email of user is ", email)
  const [isUpdate, setIsUpdate] = useState(false)
  const [dataUser, setdataUser] = useState({})
  const apiUrl= `https://store-wbly.onrender.com/userbyemail/${email}`
  // const apiId=`https://store-wbly.onrender.com/user/${user_id}`
  useEffect(() => {
    const fetchData = async () => {

      await axios.get(apiUrl)
        .then((response) => {
          console.log("the status is ", response.status)
          console.log("response is ", response.data)
          setdataUser(response.data)
        }

        )
        .catch((err) =>
          console.log("No data Fetching", err)
        )};
         fetchData()
  }, []);

  console.log("the image is ->>", dataUser.image_url)

  //  update pic ////////////////////////////////////////////////////////////////////////////////////
  const imgCard =dataUser.image_url;
  const [image_url, setImageUrl] = useState(imgCard);
  const {user}=useAuth()
  const update = async (e) => {
    e.preventDefault()

    const configuration = {
      method: "Put",
      url: "https://store-wbly.onrender.com/user/" + dataUser.user_id,
      data: {
        image_url
      }

    }
    await axios(configuration)
      .then((result) => {
        console.log("the Update of image success", result.data)
        setIsUpdate(true)
      })
      .catch((err) => {
        err = new Error()
        console.log("no data")
      })
  }
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
    console.log("opened")
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };


  return (<>
    <div className="image-area">
      <div className="img-wrapper" key={dataUser.user_id}>
        <img
          onClick={update}
          src={dataUser.image_url} alt='' />
        <h2>{dataUser.full_name}</h2>
        <ul><li>
          <a
            href='#'
            onClick={openModal}
            style={{color:'white',fontSize:"1rem"}}
          >Edit</a>
          </li></ul>

        <Model isOpen={isModalOpen} onRequestClose={closeModal}
            image_url1={image_url}
            setImageUrl1={setImageUrl}
            update={update}
            isUpdate={isUpdate}
          />
      </div>
    </div>
  </>
  )

}

export default UserCart
